		assertContractStatusSuccess(t, rp, contractAddr, `"frozen"`)
		// unfreeze by vote success
		res = manageContractByVote(t, rp, privateKey, adminCount, bvm.NewContractMaintainContractOperation(contractAddr, "evm", 3))
