	if err != nil {
		t.Error(err)
		return
	}
	test := struct {
		Addr int64   `abi:"addr1"`
		Msg1 [8]byte `abi:"msg"`
	}{}

	// testLog
	sysErr := ABI.UnpackLog(&test, "sayHello", receipt1.Log[0].Data, receipt1.Log[0].Topics)
	if sysErr != nil {
		t.Error(sysErr)
		return
	}
	msg, sysErr := abi.ByteArrayToString(test.Msg1)
	if sysErr != nil {
		t.Error(sysErr)
		return
	}
	assert.Equal(t, int64(1), test.Addr, "解码失败")
