func TestMPC_GetHis(t *testing.T) {
	rpc := NewRPC()
	key, err := account.NewAccountFromAccountJSON(accountJsons[0], pwd)
	assert.Nil(t, err)
	opt := bvm.NewMPCHistoryOperation(common.CurveNameBN254)
	payload := bvm.EncodeOperation(opt)
	tx := NewTransaction(key.GetAddress().Hex()).Invoke(opt.Address(), payload).VMType(BVM)
	res, serr := rpc.SignAndInvokeContract(tx, key)
	assert.Nil(t, serr)
	ret := bvm.Decode(res.Ret)
	assert.True(t, ret.Success)
	t.Log(string(ret.Ret))
}

//see flato-vc/mpc.TestSRS_All
const srsCon1Base = ""

func TestMPC_Beacon(t *testing.T) {
	rpc := NewRPC()
	key, err := account.NewAccountFromAccountJSON(accountJsons[0], pwd)
	assert.Nil(t, err)

	resb, err := base64.StdEncoding.DecodeString(srsCon1Base)
	assert.Nil(t, err)

	opt := bvm.NewMPCBeaconOperation(resb)
	payload := bvm.EncodeOperation(opt)
	tx := NewTransaction(key.GetAddress().Hex()).Invoke(opt.Address(), payload).VMType(BVM)
	res, serr := rpc.SignAndInvokeContract(tx, key)
	assert.Nil(t, serr)
	assert.Nil(t, serr)
	ret := bvm.Decode(res.Ret)
	assert.True(t, ret.Success)
	t.Log(string(ret.Ret))
}

