func TestSolc(t *testing.T) {
	input := []byte(`{}`)
	proof := []byte(" {\"PublicInput\":[\"7\",\"-1\"],\"PlonkProof\":\"\"}")
	limitTag := []byte("ORJHtr52aZsR2qLWwhG4lOFcG6u9xCGYSFn0U1msmZ3/3Gimwo5gIAiHIoKKbcFjDhatxlEMqyfJDmSk73XKOQ==")
	ABI, er := abi.JSON(strings.NewReader(abiAdd))
	assert.Nil(t, er)
	packed, er := ABI.Pack("verifyProof", input, proof, limitTag)
	assert.Nil(t, er)
	res := hex.EncodeToString(packed)
	fmt.Println(string(res))
}
